import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import Card from '@material-ui/core/Card';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';

class PostsItem extends Component {

    constructor(props) {
        super(props);
    }

    render() {
        const styles ={
            Grid:{
                marginTop: 20,
                padding: 40,
            },
            Heading:{
                lineHeight: 1,
            }
        };

        return (
            <Grid item>
                    <Card style={styles.Grid}>
                       <Typography variant={"headline"}><h1 style={styles.Heading}>{this.props.postInfo.title}</h1></Typography>
                        <Typography variant={"title"}><p>{this.props.postInfo.body}</p></Typography>
                        <Link className="Link" to={`/user/${this.props.postInfo.userId}/post/${this.props.postInfo.id}/comments/`}><Button variant={"contained"} color={"primary"}>Show comments</Button></Link>
                    </Card>
            </Grid>
        )
    }
}


export default PostsItem;
