import React, {Component} from 'react';
import UserItem from './UserItem/UsersItem';
import LinearProgress from '@material-ui/core/LinearProgress';
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';



class Users extends Component {
    constructor(props) {
        super(props);
        this.state = {
            usersList: [],
        }
    }

    componentDidMount() {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(response => response.json())
            .then(json => {
                this.setState({
                    usersList: json,
                })
            })
    }

    render() {

        let users = this.state.usersList.map((item) => {
            return <UserItem key={item.id} userInfo={item}/>
        });
        return (
            this.state.usersList.length === 0 ? <LinearProgress/> : <Card><Grid container justify="space-around" direction={"column"}>
                {users}
            </Grid></Card>
        )
    }
}

export default Users;
