import React, {Component, Fragment} from 'react';
import Grid from '@material-ui/core/Grid/Grid';
import Chatkit from '@pusher/chatkit-client';
import ConfigFile from '../../config/config';
import ChannelsComponent from "./ChannelsComponent/ChannelsComponent";
import InputComponent from "./InputComponent/InputComponent";
import CreateChannel from "./CreateChannel/CreateChannel";
import ChatComponent from "./ChatComponent/ChatComponent";


class AppContainer extends Component {

    constructor(props) {
        super(props);
        this.state = {
            channelsList: [],
            currentChannelId: "",
            currentUserId: "",
            currentMessages: [],
        };
    }

    componentDidMount() {
        const tokenProvider = new Chatkit.TokenProvider({
            url: ConfigFile.tokenEndpoint
        });

        const chatManager = new Chatkit.ChatManager({
            instanceLocator: ConfigFile.InstanceLocator,
            userId: "User1",
            tokenProvider: tokenProvider
        });

        chatManager
            .connect()
            .then(currentUser => {
                this.currentUser = currentUser;
                this.getChannels();
                this.changeChannel();
            })
            .catch(error => {
                console.error("error:", error);
            });
    }

    getChannels = () => {
        this.setState({channelsList: this.currentUser.rooms,})
    };

    changeChannel = (channelId) => {
        this.state.currentMessages({currentMessages: []});
        this.currentUser.subscribeToRoomMultipart({
            roomId: channelId,
            hooks: {
                onMessage: message => {
                    this.setState({
                        currentChannelId: channelId,
                        currentUserId: this.currentUser.encodedId,
                        currentMessages: [...this.state.currentMessages, message],
                    })
                }
            },
            messageLimit: 10,
        });

    };

    sendMessage = (event, message) => {
        console.log(message);
        this.currentUser.sendSimpleMessage({
            text: message,
            roomId: this.state.currentChannelId
        });
    };

    render() {
        let inputStyle = {
            marginLeft: '10px',
        };
        return (
            <Fragment>
                <Grid container direction={"row"} wrap={"nowrap"}>
                    <Grid item><ChannelsComponent changeChannel={this.changeChannel} channelsList={this.state.channelsList}/></Grid>
                    <Grid item xs={10}><ChatComponent currentMessages={this.state.currentMessages}/></Grid>
                </Grid>
                <Grid container direction={"row"} alignContent={"center"} alignItems={"center"} wrap={"nowrap"}>
                    <Grid item><CreateChannel/></Grid>
                    <Grid item xs={10} style={inputStyle}><InputComponent sendMessage={this.sendMessage}/></Grid>
                </Grid>
            </Fragment>
        )
    }
}

export default AppContainer;
